<?phpdefined('BASEPATH') OR exit('No direct script access allowed');class Admin_login extends CI_Controller {        public function __construct() {        parent::__construct();        $logged_info = $this->session->userdata('logged_info');        if ($logged_info != FALSE) {            redirect('dashboard', 'refresh');        }        $this->load->model('admin_models/useradmin_model', 'useradmin_mdl');    }        public function index() {        $data = array();        $data['title'] = 'Login';        $this->load->view('admin_views/admin_login/admin_login_v', $data);    }        public function check_admin_login() {        $this->form_validation->set_rules('username_or_email_address', 'Name or Email address', 'trim|required|max_length[50]');        $this->form_validation->set_rules('password', 'password', 'trim|required|max_length[20]');                if ($this->form_validation->run() == FALSE) {            $sdata['exception'] = validation_errors();            $this->session->set_userdata($sdata);            redirect('admin', 'refresh');        } else {            $result = $this->useradmin_mdl->check_login_info();            if ($result) {                $sdata['admin_id'] = $result->Id;                $sdata['admin_name'] = $result->Name;                $sdata['email'] = $result->Email;                $sdata['role'] = $result->Role_Id;                $sdata['logged_info'] = TRUE;                $this->session->set_userdata($sdata);                redirect('dashboard', 'refresh');            } else {                $sdata['exception'] = 'The <b>Username</b> or <b>Password</b> youâ€™ve entered doesnâ€™t match any account !';                $this->session->set_userdata($sdata);               // redirect('admin', 'refresh');                redirect('', 'refresh');            }        }    }}