<?phpdefined('BASEPATH') OR exit('No direct script access allowed');class Booktrip_model extends CI_Model { public function __construct() {        parent::__construct();    }    private $_book_trip = 'tbl_book_trip_link';        public function count_total_upcoming_trip() {        $result = $this->db->get_where($this->_book_trip, array('b_l_t_status' => 1,'b_l_t_delete' => 0));        if ($result->num_rows() > 0) {            return $result->num_rows();        } else {            $result = 0;            return $result;        }            }    public function count_total_ongoing_trip() {        $result = $this->db->get_where($this->_book_trip, array('b_l_t_status' => 2,'b_l_t_delete' => 0));        if ($result->num_rows() > 0) {            return $result->num_rows();        } else {            $result = 0;            return $result;        }            }    public function count_total_completed_trip() {        $result = $this->db->get_where($this->_book_trip, array('b_l_t_status' => 3,'b_l_t_delete' => 0));        if ($result->num_rows() > 0) {            return $result->num_rows();        } else {            $result = 0;            return $result;        }            }    public function count_total_cancel_trip() {        $result = $this->db->get_where($this->_book_trip, array('b_l_t_status' => 4,'b_l_t_delete' => 0));        if ($result->num_rows() > 0) {            return $result->num_rows();        } else {            $result = 0;            return $result;        }            }	}